mapper.select:

		select userid,targetuserid,message,writedate from(
select userid,targetuserid,message,writedate,rank()over(partition by userid order by writedate desc) as rank 
from message where targetuserid = #{targetuserid}
) where rank = 1 order by writedate desc


		select * from (
select * from message where userid = #{userid} and targetuserid = #{targetuserid}
union
select * from message where userid = #{targetuserid} and targetuserid = #{userid}
)
order by writedate asc
